/*
 * @lc app=leetcode.cn id=15 lang=golang
 *
 * [15] 三数之和
 *
 * https://leetcode-cn.com/problems/3sum/description/
 *
 * algorithms
 * Medium (25.52%)
 * Likes:    1816
 * Dislikes: 0
 * Total Accepted:    158.5K
 * Total Submissions: 620.3K
 * Testcase Example:  '[-1,0,1,2,-1,-4]'
 *
 * 给定一个包含 n 个整数的数组 nums，判断 nums 中是否存在三个元素 a，b，c ，使得 a + b + c = 0
 * ？找出所有满足条件且不重复的三元组。
 *
 * 注意：答案中不可以包含重复的三元组。
 *
 *
 *
 * 示例：
 *
 * 给定数组 nums = [-1, 0, 1, 2, -1, -4]，
 *
 * 满足要求的三元组集合为：
 * [
 * ⁠ [-1, 0, 1],
 * ⁠ [-1, -1, 2]
 * ]
 *
 *
 */

// @lc code=start
func threeSum(nums []int) [][]int {
	// 1. 先排序
	// 2. 左右夹逼法
	sort.Ints(nums)
	// var res [][]int
	res := make([][]int, 0, 3)
	for k := 0; k < len(nums)-2; k++ {
		// 跳过与k-1相同的项
		for k > 0 && k < len(nums) && nums[k] == nums[k-1] {
			k++
		}
		i, j := k+1, len(nums)-1
		if k >= len(nums)-2 || (nums[k]) > 0 {
			break
		}
		for i < j {
			// 先跳过两边相同的数
			for i > k+1 && i < j && nums[i] == nums[i-1] {
				i++
			}
			for j < len(nums)-1 && j > k+2 && nums[j] == nums[j+1] {
				j--
			}
			for i < j && (nums[k]+nums[i]+nums[j]) > 0 {
				j--
			}
			for i < j && (nums[k]+nums[i]+nums[j]) < 0 {
				i++
			}
			if i < j && nums[k]+nums[i]+nums[j] == 0 {
				res = append(res, []int{nums[k], nums[i], nums[j]})
				i++
				j--
			}
		}
	}

	return res
}

